function registerControllerFromPath(e,r,t){const o=e.replace(new RegExp(`^${r}/`),"").replace("_controller","").replace(/\//g,"--").replace(/_/g,"-");import(e).then((e=>t.register(o,e.default))).catch((r=>console.error(`Failed to register controller: ${o} (${e})`,r)))}import"@hotwired/stimulus";export function registerControllersFrom(e,r){Object.keys(parseImportmapJson()).filter((r=>r.match(new RegExp(`^${e}/.*_controller$`)))).forEach((t=>registerControllerFromPath(t,e,r)))}export function parseImportmapJson(){return JSON.parse(document.querySelector("script[type=importmap]").text).imports}console.warn("stimulus-importmap-autoload.js has been deprecated in favor of stimulus-loading.js");